import React from 'react';
import ReactDOM from 'react-dom';

function MyThing() {
    return (
        <div className='book'>
          <div className='title'>
	    The Title
 	  </div>
          <div className='author'>
	    The Author
          </div>
          <ul className='stats'>
	    <li className='rating'>
	      5 starrs
	    </li>
            <li className='isbn'>
              12-345678-910
            </li>
          </ul>
          {/* 
          <div>
            &nbsp;Non-breaking
            &nbsp;Spaces&nbsp;
          </div>
           */}
          {/* Inserting spaces when content 

            span multiple lines.
          <div>
            Line1
            {' '}
            Line2
            </div> */}
        </div>
    );
}

// ReactDOM.render(
