function Greeting() {
//    let username = "root";	
//    let username = undefined;
    let username = null;
//    let username = false;

    return (
        <span>
          {username && 'Hello, username'}
          {!username && 'Not logged in'}
        </span>
    );
}

ReactDOM.render(
    <Greeting/>,
    document.getElementById('root'),
);



