import moment from 'moment';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

// add the { tweet } prop, destructured
function Tweet({ tweet }) {
    return (
        <div className="tweet">
          <Avatar hash={tweet.gravatar}/>
            <div className="content">
              <NameWithHandle author={tweet.author}/><Time time={tweet.timestamp}/>
                <Message text={tweet.message}/>
                <div className="buttons">
                    <ReplyButton/>
                    <RetweenButton/>
                    <LikeButton/>
                    <MoreOptionsButton/>
                </div>
            </div>
        </div>
    );
}

/* The gravatar hash, passed in as hash
   using ES6 destructuring, Is incorporated into
   the URL and passed to the image tag as before.
*/

function Avatar({ hash }) {
    var url = 'https://www.gravatar.com/avatar/${hash}';
    return (
        <img 
            src={url}
            className="avatar"
            alt="avatar"/>
    );
}

function NameWithHandle({ author }) {
    const { name, handle } = author;
    return (
        <span className="name-with-handle">
          <span className="name">{name}</span>
          <span className="handle">@{handle}</span>
        </span>
    );
}

function Message({ text }) {
    return (
        <div className="message">
          {text}
        </div>
    );
}

/* For single statement there is no need
   of return and the braces.

   For two statements or more, it need surrounding
   braces, which then causes it to need a return.
*/
const Time = ({ time }) => {
    const timeString = moment(time).fromNow();

    return(
        <span className="time">
          {timeString}
        </span>
    );
};

const ReplyButton = () => (
    <i className="fa fa-reply reply-button"></i>
);

const RetweenButton = () => (
    <i className="fa fa-retweet retween-button"/>
);

const LikeButton = () => (
    <i className="fa fa-heart like-button"/>
);

const MoreOptionsButton = () => (
    <i className="fa fa-ellipsis-h more-options-buttons"/>
);

// ...
var testTweet = {
    message: "Something about cats.",
    gravatar: "xyz",
    author: {
        handle: "catperson",
        name: "IAMA Cat Person"
    },
    likes: 2,
    retweets: 0,
    timestamp: "2019-7-1 21:24:37"
};

ReactDOM.render(<Tweet tweet={testTweet}/>, 
    document.querySelector('#root'));
